INSERT INTO `aliquotafcp` VALUES (1,1,0,1,'Alíquota Interna Geral','\r\n\r\nLei 6.558/2004 - Lei do Fundo Estadual de Combate e Erradicação da Pobreza - FECOEP. Art. 2º-A, §2º, Alteração pelo Comunicado SER 2/2016.\r\n\r\nO Fundo Estadual de Combate a Pobreza de 1%, será devido apenas nas operações em que as mercadorias estejam sujeitas à alíquota da regra geral.\r\n\r\nNão se aplica o FECOEP nas seguintes situações: \r\n- fornecimento de alimentação em bares, lanchonetes, restaurantes e afins;\r\n- serviços de transporte rodoviário intermunicipal de passageiros, e ao aquaviário;\r\n- fornecimento de energia elétrica residencial até 150 (cento e cinquenta) quilowatts/horas mensais, para consumo domiciliar e de estabelecimento comercial;\r\n- aos gêneros que compóem a cesta básica, relacionados no item 20 do Anexo II do Regulamento do ICMS; e \r\n- material escolar, a ser relacionado pelo Poder Executivo\r\n\r\n','Lei 6.558/2004, Artigo 2º-A','2016-01-11T00:00:00Z','null','AL'),(2,2,0,1,'Alíquota Interna Geral','\r\n\r\nLei 6.558/2004 - Lei do Fundo Estadual de Combate e Erradicação da Pobreza - FECOEP. Art. 2º-A, §2º, Alteração pelo Comunicado SER 2/2016.\r\n\r\nO Fundo Estadual de Combate a Pobreza de 1%, será devido apenas nas operações em que as mercadorias estejam sujeitas à alíquota da regra geral.\r\n\r\nNão se aplica o FECOEP nas seguintes situações: \r\n- fornecimento de alimentação em bares, lanchonetes, restaurantes e afins;\r\n- serviços de transporte rodoviário intermunicipal de passageiros, e ao aquaviário;\r\n- fornecimento de energia elétrica residencial até 150 (cento e cinquenta) quilowatts/horas mensais, para consumo domiciliar e de estabelecimento comercial;\r\n- aos gêneros que compóem a cesta básica, relacionados no item 20 do Anexo II do Regulamento do ICMS; e \r\n- material escolar, a ser relacionado pelo Poder Executivo\r\n\r\n','Lei 6.558/2004, Artigo 2º-A','2016-01-11T00:00:00Z','null','AL'),(3,3,0,1,'Alíquota Interna Geral','\r\n\r\nLei 6.